def dig_pow(n, p):
    res = (sum([int(x)**(i+p) for i, x in enumerate(str(n))]) / n)
    return int(res) if res.is_integer() else -1

def your_order(sentence):
    return ' '.join(sorted(sentence.split(), key=lambda w: sorted(w)))
#utilizes the fact that numbers are always before letters in ASCII

function disemvowel(str) {
  return str.split('').map((l) => !'aeiou'.split('').includes(l) ? l : '').join('');
}

function disemvowel2(str) {
  return str.replace(/[aeiou]/gi, '');

function findOdd2(A) {
  cDict = {}  
  A.forEach((x) => {!cDict[x] ? cDict[x] = 1 : cDict[x] += 1 })
  return parseInt(Object.keys(cDict).find(key => cDict[key] % 2 !== 0));
}

